function echart_options(step, CORE_STEPS, MAP_COOR) {
    let current_points = CORE_STEPS[step];
    return {
        graphic: MAP_COOR.map(function (item) {
            return { // Position the entire rotated group at the right-bottom corner of its container.
                type: 'group',
                // rotation: Math.PI / 4,
                // right: item.x * 0.8 - 10,  // Position at the right boundary.
                // bottom: item.y * 0.8 - 40, // Position at the bottom boundary.
                position: [item.x * 0.8 - 0 , item.y * 0.8 - 40],
                // position: [item.x * 0.8, item.y * 0.8],
                // Scale, using [1, 1] by default.
                // scale: [0.6, 0.6],

                children: [
                    {
                        type: 'circle',
                        shape: {
                            cx: 0,
                            cy: 0,
                            r: 10
                        },
                        style: {
                            fill: current_points.includes(item.point) ? '#8ec3d8' : '#fff', 
                            stroke: '#999',
                            lineWidth: 2,
                            shadowBlur: 8,
                            shadowOffsetX: 3,
                            shadowOffsetY: 3,
                            shadowColor: 'rgba(0,0,0,0.3)'
                        }
                    },
                    {
                        type: 'text',
                        left: 'center', // Position at horizontal center according to its parent.
                        top: 'middle',  // Position at vertical center according to its parent.
                        style: {
                            fill: '#777',
                            text: item.point,
                            font: '14px Microsoft YaHei'
                        }
                    }
                ]
            }
        })
    }
}

