// Import shorthands from xstate object
const { assign } = xstate; 

// First define names guards, actions, ...

/**
 * Guards
 */
const bellyFull = (context, event) => {
  return context.belly >= 100;
};

const bellyEmpty = (context, event) => {
    return context.belly <= 0;
}

const bellyNotEmpty = (context, event) => {
    return context.belly > 0;
}

/**
 * Actions
 */
const updateBelly = assign({
    belly: (context, ev) => Math.max(
        Math.min(
            context.belly+ev.value,100
        ), 0)
});

/**
 * Activities
 */
const meow = () => {
  const interval = setInterval(() => node.send({payload: "MEOW!"}), 2000);
  return () => clearInterval(interval);
};

/**
 * Services
 * see https://xstate.js.org/docs/guides/communication.html#invoking-callbacks
 */
const eat = (ctx, ev) => (cb) => {
    const id = setInterval(() => cb({
      type: 'eaten',
      value: 5
    }),500);
    return () => clearInterval(id);
};

const digest = (ctx, ev) => (cb) => {
    const id = setInterval(() => cb({
      type: 'digested',
      value: -5
    }),500);
    return () => clearInterval(id);
}

/***************************
 * Main machine definition * 
 ***************************/
 return {
  machine: {
    context: {
        belly: 0    // Belly state, 100 means full, 0 means empty
    },
    initial: 'sleep',
    states: {
      sleep: {
          on: {
              'wake up': 'meow',
          }
      },
      meow: {
          invoke: { src: 'digest' },
          on: {
              'given toy': { target: 'play', cond: 'belly not empty' },
              'given food': 'eat',
              'digested': { actions: 'updateBelly' }
          },
          activities: ['meow']
      },
      play: {
          invoke: { src: 'digest' },
          on: {
              tired: 'sleep',
              bored: 'sleep',
              'digested': { actions: 'updateBelly' },
              '': { target: 'meow', cond: 'belly empty' }
          },
          after: {
              5000: 'sleep',
          }
      },
      eat: {
          invoke: { src: 'eat' },
          on: {
              '': { target: 'sleep', cond: 'belly full' },
              'no more food': { target: 'meow' },
              'eaten': { actions: 'updateBelly' }
          }
      }
    }
  },
  // Configuration containing guards, actions, activities, ...
  // see above
  config: {
      guards: { "belly full": bellyFull, "belly not empty": bellyNotEmpty, "belly empty": bellyEmpty },
      activities: { meow },
      actions: { updateBelly },
      services: { eat, digest }
  }
}

// Available variables/objects/functions:
// xstate
// - .Machine
// - .interpret
// - .assign
// - .send
// - .sendParent
// - .spawn
// - .raise
// - .actions
//
// Common
// - setInterval, setTimeout, clearInterval, clearTimeout
// - node.send, node.warn, node.log, node.error
// - context.get, context.set
// - flow.get, flow.set
// - env.get
// - util

const { assign } = xstate;

// First define names guards, actions, ...

/**
 * Guards
 */
const maxValueReached = (context, event) => {
  return context.counter >= 10;
};

/**
 * Actions
 */
const incrementCounter = assign({
  counter: (context, event) => context.counter + 1
});

const resetCounter = assign({
  counter: (context, event) => {
    // Can send log messages via
    //  - node.log
    //  - node.warn
    //  - node.error
    //node.warn("RESET");

    // Can send messages to the second outport
    // Specify an array to send multiple messages
    // at once
    //  - node.send(msg)
    node.send({ payload: "resetCounter" });

    return 0;
  }
});

/**
 * Activities
 */
const doStuff = () => {
  // See https://xstate.js.org/docs/guides/activities.html
  const interval = setInterval(() => {
    node.send({ payload: 'BEEP' });
  }, 1000);
  return () => clearInterval(interval);
};

/***************************
 * Main machine definition * 
 ***************************/
return {
  machine: {
    context: {
      counter: 0
    },
    initial: 'run',
    states: {
      run: {
        initial: 'count',
        states: {
          count: {
            on: {
              '': { target: 'reset', cond: 'maxValueReached' }
            },
            after: {
              1000: { target: 'count', actions: 'incrementCounter' }
            }
          },
          reset: {
            exit: 'resetCounter',
            after: {
              5000: { target: 'count' }
            },
            activities: 'doStuff'
          }
        },
        on: {
          PAUSE: 'pause'
        }
      },
      pause: {
        on: {
          RESUME: 'run'
        }
      }
    }
  },
  // Configuration containing guards, actions, activities, ...
  // see above
  config: {
    guards: { maxValueReached },
    actions: { incrementCounter, resetCounter },
    activities: { doStuff }
  },
  // Define listeners (can be an array of functions)
  //    Functions get called on every state/context update
  listeners: (data) => {
    //node.warn(data.state + ":" + data.context.counter);
  }
};

// Import shorthands from xstate object
const { assign } = xstate;

// 定义名称守卫、动作、服务等

// 守卫
const cmdSent = context => context.cmdSent;
const respReceived = context => context.respReceived;

// 动作
const sendCmd = assign({ cmdSent: true });
const showResp = assign({ respReceived: true });

// 服务 
const waitResp = (ctx, ev) => (cb) => {
  setTimeout(() => {
    cb({ type: 'respReceived' });
  }, 3000);
}

// 状态机定义
return {
  machine: {
    context: {
      cmdSent: false,
      respReceived: false
    },
    initial: 'ready',
    states: {
      ready: {
        on: {
          'sendCmd': {
            target: 'waitingResp',
            actions: sendCmd
          }
        }
      },
      waitingResp: {
        invoke: { src: 'waitResp' },
        on: {
          'respReceived': {
            target: 'ready',
            actions: showResp
          },
          'timeout': 'timeout'
        }
      },
      timeout: {
        on: {
          'respReceived': {
            target: 'ready',
            actions: showResp
          }
        }
      }
    }
  },
  config: {
    guards: {
      "cmdSent": cmdSent,
      "respReceived": respReceived
    },
    actions: { sendCmd, showResp },
    services: { waitResp }
  }
}

